import React, { useEffect, useState } from 'react';
import { saveAs } from 'file-saver'

let sourceimage = ""
let nameoftype = ""

const PW = () => {
   const hro = 1000;
   const g = 9.81;
   const [Q, setQ] = useState('')
   const [Н, setН] = useState('')
   const [ηt, setηt] = useState('')
   const [Pt, setPt] = useState('')
   function calQ(e) { setQ(e.target.value) }
   function calН(e) { setН(e.target.value) }
   function calηt(e) { setηt(e.target.value) }
   const [Pe, setPe] = useState()
   const [Q1, setQ1] = useState('')
   const [Н1, setН1] = useState('')
   const [hg, Sethg] = useState()
   const [hj, Sethj] = useState()
   const [et, Setet] = useState()
   const [eg, Seteg] = useState('')
   const [ƒt, Setƒt] = useState()
   const [ƒi, Setƒi] = useState('')

   function calƒi(e) { Setƒi(e.target.value) }
   function calƒt(e) { Setƒt(e.target.value) }
   function calhg(e) { Sethg(e.target.value) }
   function calhj(e) { Sethj(e.target.value) }
   function caleg(e) { Seteg(e.target.value) }
   function calet(e) { Setet(e.target.value) }
   function calQ1(e) { setQ1(e.target.value) }
   function calН1(e) { setН1(e.target.value) }
   useEffect(() => {
      setPt(Number(hro) * Number(g) * Number(Q) * Number(Н) * Number(ηt))
  
     if (Q.length > 0  ) { localStorage.setItem("input", Q );}
      setQ(localStorage.getItem("input"))
   }, [ηt, hro, g, Q, Н])

   useEffect(() => {
      setPe(Number(hro) * g * Number(Q1) * (Number(Н1) - Number(hg) + Number(hj)) * Number(et) * Number(eg) * (1 - Number(ƒt)) * (1 - Number(ƒi)))
   }, [hro, g, Q1, Н1, hg, hj, et, eg, ƒt, ƒi])



   if (ηt <= 55) {
      nameoftype = "Пелтон (ковшова)"
      sourceimage = "https://www.tyazhmash.com/r/_content/32013ce4ad24862062adcfc2daf60c2e/pelton-2.png"

   }
   if (ηt <= 65 && ηt > 55) {
      nameoftype = "Тюрго"
      sourceimage = "https://tamarhydro.com.au/wp-content/uploads/2015/06/Turgo_Hydro_Turbines1.png"

   }
   if (ηt <= 75 && ηt > 65) {
      nameoftype = "Томсон (пропе-лерна)"
      sourceimage = "https://st.depositphotos.com/1734770/4166/i/600/depositphotos_41661599-stock-photo-speedboat-propeller.jpg"
   }
   if (ηt <= 80 && ηt > 75) {
      nameoftype = "Шнекова"
      sourceimage = "https://img.directindustry.com/images_di/photo-mg/29492-8247036.jpg"
   }
   if (ηt <= 85 && ηt > 80) {
      nameoftype = "Каплан (пово-ротно-лопатева)"
      sourceimage = "https://www.tyazhmash.com/r/_content/3b9d96849c21d023021a1a2350b55d41/kaplan-8-meters-v2-s.png"
   }
   if (ηt <= 95 && ηt > 85) {
      nameoftype = "Френсіс (радіально-осьова)"
      sourceimage = "https://www.tyazhmash.com/f/1/products/hydro/hydraturbines/francis/francis.png"
   }

   const file = new Blob(["Потужність, яка розвивається гідротурбіною(Pt): " + Pt + " Вт"], {
      type: "text/plain;charset=utf-8"
   });
   const eventSaving = () => {
      saveAs(file, "alo.txt")
   }
  
   return <>
      <div className="main_containar">
         <div className="PowerTurbin">
            <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input value={Pt} className="Formula№1Result" type="text" name="Pt" placeholder="Pt" readOnly />  Вт</h1>
            <ul className="Formula№1Variables">
               <li> &rho; – густина води <input value={hro} className="hro" type="text" name="hro" readOnly />кг/м<sup>3</sup> </li>
               <li> g – прискорення вільного падіння <input value={g} className="g" type="text" name="g" readOnly />м/c<sup>2</sup></li>
               <li> Q – витрата води <input value={Q} onChange={calQ} className="Q" type="text" name="Q" placeholder="Q" maxLength="4" />л/с</li>
               <li> Н – робочий напір  <input id="inp" value={Н} onChange={calН} className="eH" type="text" name="Н" placeholder="Н" maxLength="4" />м</li>
               <li> η<sub>T</sub> – повний ККД турбіни  <input value={ηt} onChange={calηt} className="ηt" type="text" name="ηt" readOnly placeholder="ηT" /></li>
            </ul>
            <input id="inp" className="KKD" type="range" max="95" step="5" onChange={calηt} name="KKD" />
            <label className="LabelTypeOfTurbins">Рекомендована турбіна під ККД: {nameoftype}</label>
            <div className="TypeTurbinsImage">   <img className="TypeTurbinsCurrentImg" src={sourceimage} alt="Тип Турбіни" /></div>
            <div className="Formula№1Formulas">
               <label> P<sub>т</sub> = {!ηt ? <span>&rho;</span> : 1000}  ⋅
   {!ηt ? <span>g</span> : 9.81}  ⋅
   {!Q ? <span>Q</span> : Q}  ⋅
    {!Н ? <span>Н</span> : Н}  ⋅
    {!ηt ? <span>η <sub>T</sub></span> : ηt}</label>
            </div>
         </div>
         <div className="ElectricPowerGenerator">
            <h1 className="Formula№2Header" > Електрична потужність генератора <input value={Pe} className="Formula№2Result" type="number" name="Pe" readOnly placeholder="Pe" /> Вт  </h1>
            <div className="Formula№2Variables">
               <li> &rho; – густина води <input value={hro} className="hro" type="text" name="hro" readOnly />кг/м<sup>3</sup> </li>
               <li> g – прискорення вільного падіння <input value={g} className="g" type="text" name="g" readOnly />м/c<sup>2</sup></li>
               <li> Q – витрата води <input value={Q1} onChange={calQ1} className="Q" type="text" name="Q" placeholder="Q" maxLength="4" />л/с</li>
               <li> Н – робочий напір  <input value={Н1} onChange={calН1} className="eH" type="text" name="Н1" placeholder="Н" maxLength="4" />м</li>
               <li>  h <sub>ƨ</sub> – гідравлічні втрати  <input value={hg} onChange={calhg} className="hg" type="number" placeholder="hg" /></li>
            </div>
            <div className="Formula№2Variables№2">
               <li>  h <sub>j</sub> – інші втрати  <input value={hj} onChange={calhj} className="hj" type="text" maxLength="4" placeholder="hg" /></li>
               <li>  e<sub>t</sub> – ефективність генератора  <input value={et} onChange={calet} className="et" type="text" maxLength="4" placeholder="et" /></li>
               <li>  ƒ<sub>t</sub> – втрати трансформатора  <input value={ƒt} onChange={calƒt} className="ƒt" type="text" maxLength="4" placeholder="ƒt" /></li>
               <li>  ƒ<sub>i</sub> – інші втрати електроенергії  <input value={ƒi} onChange={calƒi} className="ƒi" type="text" maxLength="4" placeholder="ƒi" /></li>
               <li>  e<sub>g</sub> – коефіцієнт використання при СБС  Q  <input value={eg} onChange={caleg} className="eg" type="text" placeholder="eg" maxLength="4" /></li>
            </div>
            <div className="Formula№2Formulas">
               <label> P<sub>E</sub> = {!ƒi ? <span>&rho;</span> : 1000} ⋅ {!ƒi ? <span>g</span> : 9.81} ⋅ {!Q1 ? <span>Q</span> : Q1} ⋅ [{!Н1 ? <span>H</span> : Н1} -
      ({!hg ? <span>h<sub>g</sub></span> : hg} + {!hj ? <span>h<sub>j</sub></span> : hj} )] ⋅ {!et ? <span>e<sub>t</sub></span> : et} ⋅ {!eg ? <span>e<sub>g</sub></span> : eg} ⋅
      (1 - {!ƒt ? <span>ƒ<sub>t</sub></span> : ƒt}) ⋅ (1 - {!ƒi ? <span>ƒ<sub>i</sub></span> : ƒi}) </label>
            </div>
         </div>




         <div>
          <button  onClick={eventSaving}> Завантажити </button>
        </div>
      </div>
   </>
}


export default PW
