import React, {useEffect, useState} from 'react';
const EPG = () => {

    const[Pe, setPe] = useState()
    const[hro, Sethro] = useState()
    const[g, Setg] = useState()
    const[Q, SetQ] = useState()
    const[Н, SetН] = useState()
    const[hg, Sethg] = useState()
    const[hj, Sethj] = useState()
    const[et, Setet] = useState()
    const[eg, Seteg] = useState('')
    const[ƒt, Setƒt] = useState()
    const[ƒi, Setƒi] = useState()


    function calhro(e) { Sethro(e.target.value)}
    function calg(e) { Setg(e.target.value)}
    function calQ(e) { SetQ(e.target.value)}
    function calН(e) { SetН(e.target.value)}
    function calhg(e){ Sethg(e.target.value)}
    function calhj(e) { Sethj(e.target.value)}
    function caleg(e) { Seteg(e.target.value)}
    function calet(e) { Setet(e.target.value)}
    function calƒt(e) { Setƒt(e.target.value)}
    function calƒi(e) { Setƒi(e.target.value)}

    useEffect(()=> {
        setPe(Number(hro) * Number(g)*Number(Q) * (Number(Н) - Number(hg)+Number(hj)) * Number(et) * Number(eg) * (1 - Number(ƒt)) * ( 1 - Number(ƒi)))
    },[ hro,g,Q,Н,hg,hj,et,eg,ƒt,ƒi])
    return  <> 


<h1 className="Formula№2Header" > Електрична потужність генератора <input   value= {Pe}  className="Formula№2Result" type="number" name="Pe" readOnly placeholder="Pe"/> Вт  </h1>
    <div>
       
<div className="Formula№2Variables">
<li>  h <sub>ƨ</sub> – гідравлічні втрати  <input  value = {hg}  className="hg" type="number"  readOnly   placeholder="hg" /></li>
<li>  h <sub>j</sub> – інші втрати  <input  value = {hj} className="hj" type="number"   readOnly pleholder="hj" /></li>
<li>  e<sub>t</sub> – ефективність генератора  <input  value = {et}  className="et" type="number"  readOnly placeholder="et" /></li>
<li>  ƒ<sub>t</sub> – втрати трансформатора  <input  value = {ƒt}  className="ƒt" type="number" readOnly  placeholder="ƒt" /></li>
        <li>  ƒ<sub>i</sub> – інші втрати електроенергії  <input  value = {ƒi}  className="ƒi" type="number"  readOnly placeholder="ƒi" /></li>
        {/* <li> &rho; = 1000 кг/м3 – густина води: <input value = {hro} onChange={calhro} className="hro" type="number" name="hro"  placeholder="&rho;" /></li>
        <li> g = 9,81 м/с2 – прискорення вільного падіння <input  value = {g} onChange={calg} className="input" type="number" name="g"  placeholder="g" /></li>
       <li>  e<sub>g</sub> – коефіцієнт використання при СБС  Q  <input  value = {eg} onChange={caleg} className="eg" type="text"  placeholder="eg" maxLength="4"  /></li>
        <li>Q – витрата води <input value = {Q} onChange={calQ} className="Q" type="number" name="Q"  placeholder="Q" /></li>
        <li>Н – робочий напір <input  value = {Н} onChange={calН} className="Н" type="number" name="Н"  placeholder="Н" /></li>
       
     */}
</div>

<div className="Formula№2Formulas">
<label> P<sub>E</sub> = {!hro? <span>&rho;</span>:hro} ⋅ {!g? <span>g</span>:g} ⋅ {!Q? <span>Q</span>:Q} ⋅ [{!Н? <span>H</span>:Н} - 
 ({!hg? <span>h<sub>g</sub></span>:hg} + {!hj? <span>h<sub>j</sub></span>:hj} )] ⋅ {!et? <span>e<sub>t</sub></span>:et} ⋅ {!eg? <span>e<sub>g</sub></span>:eg} ⋅
 (1 - {!ƒt? <span>ƒ<sub>t</sub></span>:ƒt}) ⋅ (1 - {!ƒi? <span>ƒ<sub>i</sub></span>:ƒi}) </label>
</div>


    </div>
    
    
    
    
    </>
}

export default EPG
