import React, {useEffect, useState} from 'react';

 

let sourceimage = ""
let nameoftype = ""


const ElectricPowerGenerator = () => {
    
   const hro = 1000;
    const g = 9.81;
    const[Q, setQ] = useState('')
    const[Н, setН] = useState('')
    const[ηt, setηt] = useState('')
    const[Pt, setPt] = useState('')
 
   
    function calQ(e){setQ(e.target.value)}
    function calН(e){setН(e.target.value)}
    function calηt(e){setηt(e.target.value)}

        useEffect(()=> {
            setPt(Number(hro)*Number(g)*Number(Q)*Number(Н)*Number(ηt))
        },[ηt , hro , g , Q , Н ])


      //   if (   ηt <= 55    ){
      //       nameoftype = "Пелтон (ковшова)"
      //        document.getElementsByClassName('TypeTurbinsCurrentImg').src = "bgimg.png"
         
      //    }
      //    if (   ηt <= 65 && ηt > 55    ){
      //        nameoftype = "Тюрго"
      //        sourceimage = "https://lh3.googleusercontent.com/proxy/fvITSIs_39Gz7KFWbZQ3VV68Y_TFdfBJAHZufFheJbbmtEMfbcJ2HWljBkkce5UPN5qvMQFWCIMG33k6YzvQ9HHPdc7jd_Te-qz6SGKoPi2JtmUl1g"
           
      //     }
      //     if (   ηt <= 75 && ηt > 65    ){
      //        nameoftype = "Томсон (пропе-лерна)"
      //        sourceimage = "https://www.tyazhmash.com/r/_content/3b9d96849c21d023021a1a2350b55d41/kaplan-8-meters-v2-s.png"
      //     }
      //     if (   ηt <= 80 && ηt > 75    ){
      //        nameoftype = "Шнекова"
      //     }
      //     if (   ηt <= 85 && ηt > 80    ){
      //        nameoftype = "Каплан (пово-ротно-лопатева)"
      //        sourceimage = "https://www.tyazhmash.com/r/_content/35c752e03416c5e162a665f256fb42b8/kaplan-8-meters-s.png"
      //     }
      //     if (     ηt <= 95 &&  ηt > 85   ){
      //        nameoftype = "Френсіс (радіально-осьова)"
      //        sourceimage = "https://lh3.googleusercontent.com/proxy/oJgo7jLNsZkK9jbIeO4IOCXvNMSX2fjQT54TQz-y1fgqNnMIToFe0u15b3wXjHQ94h6WYxRubeK23uUKto2iSvbp8LUnkTarjr815iBeHlpEXEs4Oou3YzC7jMnOrHQ"
      //     }

    return <>

    <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input  value= {Pt}  className="Formula№1Result" type="text" name="Pt" placeholder="Pt" readOnly/>  Вт</h1>
    
    <ul className="Formula№1Variables">
       <li> &rho; – густина води <input value = {hro}  className="hro" type="text" name="hro" readOnly placeholder="&rho; = 1000"/>кг/м<sup>3</sup> </li>
       <li> g – прискорення вільного падіння <input  value = {g} className="g" type="text" name="g" readOnly  placeholder="g=9.81" />м/c<sup>2</sup></li>
       <li> Q – витрата води <input value = {Q} onChange={calQ} className="Q" type="text" name="Q"  placeholder="Q" maxLength="4"  />л/с</li>
       <li> Н – робочий напір  <input  value = {Н} onChange={calН} className="eH" type="text" name="Н"  placeholder="Н" maxLength="4"  />м</li>
       <li> η<sub>T</sub> – повний ККД турбіни  <input  value = {ηt} onChange={calηt} className="ηt" type="text" name="ηt" readOnly placeholder="ηT"/></li>
   </ul>

   <input  className="KKD" type="range"  max="95" step="5" onChange={calηt} name="KKD"/>
     <label className="LabelTypeOfTurbins">Рекомендована турбіна під ККД: {nameoftype}</label>



     <div className="TypeTurbinsImage">   <img className="TypeTurbinsCurrentImg" src = 'bgimg.png' alt="Тип Турбіни"/></div>
   <div className="Formula№1Formulas"> 
   <label> P<sub>т</sub> = {!ηt? <span>&rho;</span>:1000}  ⋅ 
   {!ηt? <span>g</span>:9.81}  ⋅ 
   {!Q? <span>Q</span>:Q}  ⋅
    {!Н? <span>Н</span>:Н}  ⋅ 
    {!ηt? <span>η <sub>T</sub></span>:ηt}</label>
       </div>
    
    
    </> 
 }


 export default ElectricPowerGenerator
