import React, {useEffect, useState} from 'react';
const ElectricPowerGenerator = () => {
    const[hro, sethro] = useState()
    const[g, setg] = useState()
    const[Q, setQ] = useState()
    const[Н, setН] = useState()
    const[ηt, setηt] = useState()
    const[Pt, setPt] = useState()
    function calhro(e) { sethro(e.target.value)}
    function calg(e){setg(e.target.value)}
    function calQ(e){setQ(e.target.value)}
    function calН(e){setН(e.target.value)}
    function calηt(e){
        setηt(e.target.value)}
        useEffect(()=> {
            setPt(Number(hro)*Number(g)*Number(Q)*Number(Н)*Number(ηt))
        },[ηt , hro , g , Q , Н ])

    return <>

    <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input  value= {Pt}  className="Formula№1Result" type="number" name="Pt" readOnly placeholder="Pt"/>  Вт</h1>
    
    <div className="Formula№1Variables">
    <li>
         &rho; = 1000 кг/м3 – густина води: <input value = {hro} onChange={calhro} className="input" type="number" name="hro"  placeholder="&rho;" />
          g = 9,81 м/с2 – прискорення вільного падіння <input  value = {g} onChange={calg} className="input" type="number" name="g"  placeholder="g" />
        Q – витрата води <input value = {Q} onChange={calQ} className="input" type="number" name="Q"  placeholder="Q" />
        Н – робочий напір <input  value = {Н} onChange={calН} className="input" type="number" name="Н"  placeholder="Н" />
         η<sub>T</sub> – повний ККД турбіни <input  value =  {ηt} onChange={calηt}className="input" type="range" name="ηt"  max="95" step="5"   />
        <label>{nameoftype}</label>
        <img  src={sourceimage} className="typeturbins" alt="Тип Турбіни"/>
    </li>
   </div>
    
  
    
    
    </> 
 }


 export default ElectricPowerGenerator
