import React, {useEffect, useState} from 'react';

var input = document.querySelectorAll('.input'),
  buffer = [];
for (var i = 0; input.length > i; i++) {
  console.log(input[i].value);
  buffer[i] = document.createElement('div');
  buffer[i].className = "buffer";
  input[i].parentNode.insertBefore(buffer[i], input[i].nextSibling);
  input[i].oninput = function() {
    this.nextElementSibling.innerHTML = this.value;
    this.style.width = this.nextElementSibling.clientWidth + 'px';
  };
}

// let sourceimage = ""
// let nameoftype = ""
// <label>{nameoftype}</label>
// <img  src={sourceimage} className="typeturbins" alt="Тип Турбіни"/>

const ElectricPowerGenerator = () => {
    const[hro, sethro] = useState()
    const[g, setg] = useState()
    const[Q, setQ] = useState()
    const[Н, setН] = useState()
    const[ηt, setηt] = useState()
    const[Pt, setPt] = useState()
    function calhro(e) { sethro(e.target.value)}
    function calg(e){setg(e.target.value)}
    function calQ(e){setQ(e.target.value)}
    function calН(e){setН(e.target.value)}
    function calηt(e){
        setηt(e.target.value)}
        useEffect(()=> {
            setPt(Number(hro)*Number(g)*Number(Q)*Number(Н)*Number(ηt))
        },[ηt , hro , g , Q , Н ])

    return <>

    <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input  value= {Pt}  className="Formula№1Result" type="number" name="Pt" placeholder="Pt" readOnly/>  Вт</h1>
    
    <ul className="Formula№1Variables">
       <li> &rho; – густина води <input value = {hro} onChange={calhro} className="hro" type="number" name="hro" readOnly placeholder="&rho; = 1000"/> </li>
       <li> g – прискорення вільного падіння <input  value = {g} onChange={calg} className="g" type="number" name="g"  placeholder="g" /></li>
       <li> Q – витрата води <input value = {Q} onChange={calQ} className="Q" type="number" name="Q"  placeholder="Q" /></li>
       <li> Н – робочий напір  <input  value = {Н} onChange={calН} className="eH" type="number" name="Н"  placeholder="Н" /></li>
       <li> η<sub>T</sub> – повний ККД турбіни  <input  value =  {ηt} onChange={calηt}className="ηt" type="number" name="ηt" placeholder="ηt"/></li>
        
   </ul>

   <div className="Formula№1Formulas"> 
   <label> P<sub>т</sub> = {!ηt? <span>1000</span>:hro} ⋅ 
   {!g? <span>g</span>:g} ⋅ 
   {!Q? <span>Q</span>:Q} ⋅
    {!Н? <span>Н</span>:Н} ⋅ 
    {!ηt? <span>η <sub>т</sub></span>:ηt}</label>
       </div>
    
    
    </> 
 }


 export default ElectricPowerGenerator
