import React, { useState } from 'react';
// import { saveAs } from 'file-saver'
let sourceimage = ""
let nameoftype = ""
const hro = 1000;
const g = 9.81;



const MP = () => {
   const [PE, setPE] = useState('')
   const [fj, setfj] = useState('')
   const [ft, setft] = useState('')
   const [eg, seteg] = useState('')
   const [et, setet] = useState('')
   const [hj, sethj] = useState('')
   const [hg, sethg] = useState('')
   const [Q, setQ] = useState('')
   const [Q1, setQ1] = useState('')
   const [H, setH] = useState('')
   const [H1, setH1] = useState('')
   const [ηt, setηt] = useState(5)
   const [Pt, setPt] = useState('')
   function calfj(e) { setfj(e.target.value) }
   function calft(e) { setft(e.target.value) }
   function caleg(e) { seteg(e.target.value) }
   function calet(e) { setet(e.target.value) }
   function calhj(e) { sethj(e.target.value) }
   function calhg(e) { sethg(e.target.value) }
   function calQ(e) { setQ(e.target.value) }
   function calQ1(e) { setQ1(e.target.value) }
   function calH(e) { setH(e.target.value) }
   function calH1(e) { setH1(e.target.value) }
   function calηt(e) { setηt(e.target.value) }

   function eventCalculate_1() {
      setPt(Number(hro) * Number(g) * Number(Q) * Number(H) * String(ηt))
   }


   if (ηt <= 55) {
      nameoftype = "Пелтон (ковшова)"
      sourceimage = "https://www.tyazhmash.com/r/_content/32013ce4ad24862062adcfc2daf60c2e/pelton-2.png"

   }
   if (ηt <= 65 && ηt > 55) {
      nameoftype = "Тюрго"
      sourceimage = "https://tamarhydro.com.au/wp-content/uploads/2015/06/Turgo_Hydro_Turbines1.png"

   }
   if (ηt <= 75 && ηt > 65) {
      nameoftype = "Томсон(пропе-лерна)"
      sourceimage = "https://st.depositphotos.com/1734770/4166/i/600/depositphotos_41661599-stock-photo-speedboat-propeller.jpg"
   }
   if (ηt <= 80 && ηt > 75) {
      nameoftype = "Шнекова"
      sourceimage = "https://img.directindustry.com/images_di/photo-mg/29492-8247036.jpg"
   }
   if (ηt <= 85 && ηt > 80) {
      nameoftype = "Каплан(пово-ротно-лопатева)"
      sourceimage = "https://www.tyazhmash.com/r/_content/3b9d96849c21d023021a1a2350b55d41/kaplan-8-meters-v2-s.png"
   }
   if (ηt <= 95 && ηt > 85) {
      nameoftype = "Френсіс(радіально-осьова)"
      sourceimage = "https://www.tyazhmash.com/f/1/products/hydro/hydraturbines/francis/francis.png"
   }


   return <>
      <div className="main_containar">
         <div className="PowerTurbin">
            <h1 className="Formula№1Header">Потужність, яка розвивається гідротурбіною</h1>
            <h1 className="Formula№1FormulaText"> Формула </h1>
            <b className="Formula№1Formula"> P<sub>т</sub> = <span>&rho;</span> ⋅ g ⋅ Q ⋅ Н ⋅ η <sub>T</sub></b>
            <h1 className="Formula№1FormulaVariables"> Змінні </h1>
            <b className="rho" >&rho;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;кг/м<sup>3</sup></b>
            <span class="rhotext">Густина води</span>
            <input className="rhoinput" type="number" value={hro} readOnly />
            <b className="g">g&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;м/с<sup>2</sup> </b>
            <span class="gtext">Прискорення вільного падіння</span>
            <input className="ginput" type="number" value={g} readOnly />
            <b className="Q">Q&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;л/с </b>
            <span class="Qtext">Витрата води</span>
            <input className="Qinput" type="number" value={Q} onChange={calQ}  />
            <b className="Н">Н&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;м </b>
            <span class="Htext">Робочий напір</span>
            <input className="Нinput" type="number" value={H} onChange={calH}  />
            <b className="ηT">η<sub>T</sub>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;%</b>
            <span class="ηTtext">Повний ККД турбіни</span>
            <input className="ηTinput" type="number" value={ηt} readOnly />
            <input className="KKD" type="range" min="5" max="95" step="5" onChange={calηt} />
            <h1 className="Formula№1ResultHeader"> Результат Вт: </h1>
            <input className="Formula№1Result" name="result" type="number" value={Pt} readOnly />
            <input type="submit" className="CalculateFormula№1" onClick={eventCalculate_1} value="Обчислити" />
            <label className="LabelTypeOfTurbins">Рекомендована турбіна під ККД: </label>
            <label className="LabelTypeOfTurbins1" > {nameoftype} </label>
            <div className="TypeTurbinsImage">   <img className="TypeTurbinsCurrentImg" src={sourceimage} alt="Тип Турбіни" /></div>
         </div>

         <div className="ElectricPowerGenerator">
            <h1 className="Formula№2Header">Електрична потужність генератора</h1>
            <h1 className="Formula№2FormulaText"> Формула </h1>
            <b className="Formula№2Formula"> P<sub>E</sub> = &rho;  ⋅ g ⋅ Q  ⋅ [H - ( h<sub>g</sub>  + h<sub>j</sub>)] ⋅ e<sub>t</sub>⋅ e<sub>g</sub>) ⋅(1 - ƒ<sub>t</sub> ⋅ (1 - ƒ<sub>i</sub>)</b>
            <h1 className="Formula№2FormulaVariables"> Змінні </h1>
            <b className="rho1" >&rho;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;кг/м<sup>3</sup></b>
            <span class="rhotext1">Густина води</span>
            <input className="rhoinput1" type="number" value={hro} readOnly />
            <b className="g1">g&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;м/с<sup>2</sup> </b>
            <span class="gtext1">Прискорення вільного падіння</span>
            <input className="ginput1" type="number" value={g} readOnly />
            <b className="Q1">Q&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;л/с </b>
            <span class="Qtext1">Витрата води</span>
            <input className="Qinput1" type="number" value={Q1} onChange={calQ1}/>
            <b className="DoingNap">Н&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;м </b>
            <span class="Htext1">Робочий напір</span>
            <input className="Нinput1" type="number" value={H1} onChange={calH1}  />
            <b className="hg">h<sub>g</sub></b>
            <span class="hgtext">Гідравлічні втрати</span>
            <input className="hginput" type="number" value={hg} onChange={calhg}  />
            <b className="hj">h<sub>j</sub></b>
            <span class="hjtext">Інші втрати</span>
            <input className="hjinput" type="number" value={hj} onChange={calhj}  />
            <b className="et">e<sub>t</sub></b>
            <span class="ettext">Коефіцієнт використання при середньому багаторічному стоці Q </span>
            <input className="etinput" type="number" value={et} onChange={calet}  />
            <b className="eg">e<sub>g</sub></b>
            <span class="egtext">Eфективність генератора </span>
            <input className="eginput" type="number" value={eg} onChange={caleg}  />
            <b className="ft">ƒ<sub>t</sub></b>
            <span class="fttext">Втрати трансформатора </span>
            <input className="ftinput" type="number" value={ft} onChange={calft}  />
            <b className="fj">ƒ<sub>j</sub></b>
            <span class="fjtext">Інші втрати електроенергії </span>
            <input className="fjinput" type="number" value={fj} onChange={calfj}  />
            <h1 className="Formula№2ResultHeader"> Результат Вт: </h1>
            <input className="Formula№2Result" name="result" type="number" value={PE} readOnly />
         </div>



      </div>
   </>
}


export default MP
