import React, {useEffect, useState} from 'react';

 

let sourceimage = ""
let nameoftype = ""


const PW = () => {
   const hro = 1000;
    const g = 9.81;
    const[Q, setQ] = useState('')
    const[Н, setН] = useState('')
    const[ηt, setηt] = useState('')
    const[Pt, setPt] = useState('')
    function calQ(e){setQ(e.target.value)}
    function calН(e){setН(e.target.value)}
    function calηt(e){setηt(e.target.value)}
        useEffect(()=> {
            setPt(Number(hro)*Number(g)*Number(Q)*Number(Н)*Number(ηt))
            setPe(Number(hro) * g *Number(Q) * (Number(Н) - Number(hg)+Number(hj)) * Number(et) * Number(eg) * (1 - Number(ƒt)) * ( 1 - Number(ƒi)))
        },[ηt , hro , g , Q , Н ] ,[ hro,g,Q,Н,hg,hj,et,eg,ƒt,ƒi] )
        if (   ηt <= 55    ){
            nameoftype = "Пелтон (ковшова)"
            sourceimage = "https://lh3.googleusercontent.com/QVsMAs8AKkL_RBNLvkWqaE1qPAMBalY6Z8J7OlicSltzP-WflavSs7AqqDSFphqkIcreiFl_erluKvDZjZGFGsRFvcbj7j_rpC1GHjwLYgNkjSlFhEnlDSAUuGmOHzF_559fkHRg"
         
         }
         if (   ηt <= 65 && ηt > 55    ){
             nameoftype = "Тюрго"
             sourceimage = "https://lh4.googleusercontent.com/q5SVkTNHH35gdXpcXwaVHNuwGjk7yowfoNt7Xg30UgqK0Ny65kiOnD8JhKGiSCiRcAG1xqjSImnZEelx_T6a3Pe1MN2YYsBzc5yElY29"
           
          }
          if (   ηt <= 75 && ηt > 65    ){
             nameoftype = "Томсон (пропе-лерна)"
             sourceimage = "https://lh5.googleusercontent.com/CwmlrBOvUhQitdEb2YF26-4hknl4GOaNHtfBjqRu6usROctb4TAtsfq80PTYXRZFK5unxB1iD95EWnKmG_QdB_sxo60Dv57NiYOBeRYr"
          }
          if (   ηt <= 80 && ηt > 75    ){
             nameoftype = "Шнекова"
             sourceimage ="https://lh5.googleusercontent.com/R4wAKCsV2sXc3fXbcxpH3sDbExFQim9pH28bAwMArfYbqZwIW7RC8CfcNnY86hFLgzxuSuBL_rR6HReos2DiVwg-KI4J39IkF5fBwzA7"
          }
          if (   ηt <= 85 && ηt > 80    ){
             nameoftype = "Каплан (пово-ротно-лопатева)"
             sourceimage = "https://lh4.googleusercontent.com/mj0AKcGjulMBGEWNc-u14rPgq2vkBUaLu6OJoV4uPPOwVFWW9K_uMmpbLZ1DyzofsK6_vpKeGflmW0WKe1fu25-Bbn_zLoJ3ERutqgCT"
          }
          if (     ηt <= 95 &&  ηt > 85   ){
             nameoftype = "Френсіс (радіально-осьова)"
             sourceimage = "https://lh3.googleusercontent.com/MiHAWhMq2H5BuNnZifUH9mBeBuh7BBNi0lGqrXSAeFYRqAaC8cCPzHXLkI98a2Hd0d_B-A-AWHb0kzj_EeASFHdFHQuxxqzD3_4JRyj0"
          }

    return <>

    <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input  value= {Pt}  className="Formula№1Result" type="text" name="Pt" placeholder="Pt" readOnly/>  Вт</h1>
    <ul className="Formula№1Variables">
       <li> &rho; – густина води <input value = {hro}  className="hro" type="text" name="hro" readOnly />кг/м<sup>3</sup> </li>
       <li> g – прискорення вільного падіння <input  value = {g} className="g" type="text" name="g" readOnly   />м/c<sup>2</sup></li>
       <li> Q – витрата води <input value = {Q} onChange={calQ} className="Q" type="text" name="Q"  placeholder="Q" maxLength="4"  />л/с</li>
       <li> Н – робочий напір  <input  value = {Н} onChange={calН} className="eH" type="text" name="Н"  placeholder="Н" maxLength="4"  />м</li>
       <li> η<sub>T</sub> – повний ККД турбіни  <input  value = {ηt} onChange={calηt} className="ηt" type="text" name="ηt" readOnly placeholder="ηT"/></li>
   </ul>
   <input  className="KKD" type="range"  max="95" step="5" onChange={calηt} name="KKD"/>
     <label className="LabelTypeOfTurbins">Рекомендована турбіна під ККД: {nameoftype}</label>
     <div className="TypeTurbinsImage">   <img className="TypeTurbinsCurrentImg" src = {sourceimage} alt="Тип Турбіни"/></div>
   <div className="Formula№1Formulas"> 
   <label> P<sub>т</sub> = {!ηt? <span>&rho;</span>:1000}  ⋅ 
   {!ηt? <span>g</span>:9.81}  ⋅ 
   {!Q? <span>Q</span>:Q}  ⋅
    {!Н? <span>Н</span>:Н}  ⋅ 
    {!ηt? <span>η <sub>T</sub></span>:ηt}</label>
       </div>
    </> 
 }


 export default PW
