import React, { useEffect, useState } from 'react';
import { saveAs } from 'file-saver'

let sourceimage = ""
let nameoftype = ""

const PW = () => {
   const hro = 1000;
   const g = 9.81;
   const [μH, setμH] = useState('')
   const [ε1, setε1] = useState('')
   const [φH, setφH] = useState('')
   const [S, setS] = useState('')
   const [Sc, setSc] = useState('')
   const [ε, setε] = useState('')
   const [Q, setQ] = useState('')
   const [Н, setН] = useState('')
   const [ηt, setηt] = useState('')
   const [Pt, setPt] = useState('')
   const [Pe, setPe] = useState('')
   const [Q1, setQ1] = useState('')
   const [Н1, setН1] = useState('')
   const [hg, Sethg] = useState('')
   const [hj, Sethj] = useState('')
   const [et, Setet] = useState('')
   const [eg, Seteg] = useState('')
   const [ƒt, Setƒt] = useState('')
   const [ƒi, Setƒi] = useState('')
   function calSc(e) { setSc(e.target.value) }
   function calS(e) { setS(e.target.value) }
   function calQ(e) { setQ(e.target.value) }
   function calН(e) { setН(e.target.value) }
   function calηt(e) { setηt(e.target.value) }
   function calƒi(e) { Setƒi(e.target.value) }
   function calƒt(e) { Setƒt(e.target.value) }
   function calhg(e) { Sethg(e.target.value) }
   function calhj(e) { Sethj(e.target.value) }
   function caleg(e) { Seteg(e.target.value) }
   function calet(e) { Setet(e.target.value) }
   function calQ1(e) { setQ1(e.target.value) }
   function calН1(e) { setН1(e.target.value) }
   function calε1(e) { setε1(e.target.value) }
   function calφH(e) { setφH(e.target.value) }

   useEffect(() => {
      setμH(Number(φH) * Number(ε1))
   }, [ε1, φH])

   useEffect(() => {
      setPt(Number(hro) * Number(g) * Number(Q) * Number(Н) * String(ηt))
   }, [ηt, hro, g, Q, Н])

   useEffect(() => {
      setPe(Number(hro) * g * Number(Q1) * (Number(Н1) - Number(hg) + Number(hj)) * Number(et) * Number(eg) * (1 - Number(ƒt)) * (1 - Number(ƒi)))
   }, [hro, g, Q1, Н1, hg, hj, et, eg, ƒt, ƒi])
   useEffect(() => {
      setε(Number(Sc) / Number(S))
   }, [S, Sc])



   if (ηt <= 55) {
      nameoftype = "Пелтон (ковшова)"
      sourceimage = "https://www.tyazhmash.com/r/_content/32013ce4ad24862062adcfc2daf60c2e/pelton-2.png"

   }
   if (ηt <= 65 && ηt > 55) {
      nameoftype = "Тюрго"
      sourceimage = "https://tamarhydro.com.au/wp-content/uploads/2015/06/Turgo_Hydro_Turbines1.png"

   }
   if (ηt <= 75 && ηt > 65) {
      nameoftype = "Томсон (пропе-лерна)"
      sourceimage = "https://st.depositphotos.com/1734770/4166/i/600/depositphotos_41661599-stock-photo-speedboat-propeller.jpg"
   }
   if (ηt <= 80 && ηt > 75) {
      nameoftype = "Шнекова"
      sourceimage = "https://img.directindustry.com/images_di/photo-mg/29492-8247036.jpg"
   }
   if (ηt <= 85 && ηt > 80) {
      nameoftype = "Каплан (пово-ротно-лопатева)"
      sourceimage = "https://www.tyazhmash.com/r/_content/3b9d96849c21d023021a1a2350b55d41/kaplan-8-meters-v2-s.png"
   }
   if (ηt <= 95 && ηt > 85) {
      nameoftype = "Френсіс (радіально-осьова)"
      sourceimage = "https://www.tyazhmash.com/f/1/products/hydro/hydraturbines/francis/francis.png"
   }

   const file = new Blob([" Потужність, яка розвивається гідротурбіною: " + Pt + " Вт" +
      " \n Електрична потужність генератора: " + Pe + " Вт" +
      " \n Коефіцієнт стиснення струменя рідини: " + ε + " м2" + 
      " \n Коефіцієнт витрати: " + μH  ], {
      type: "text/plain;charset=utf-8"
   });
   const eventSaving = () => {
      saveAs(file, "alo.txt")
   }





   return <>
      <div className="main_containar">
         <div className="PowerTurbin">
            <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input value={Pt} className="Formula№1Result" type="text" name="Pt" readOnly />  Вт</h1>
            <ul className="Formula№1Variables">
               <li> &rho; – густина води <input value={hro} className="hro" type="text" name="hro" readOnly />кг/м<sup>3</sup> </li>
               <li> g – прискорення вільного падіння <input value={g} className="g" type="text" name="g" readOnly />м/c<sup>2</sup></li>
               <li> Q – витрата води <input value={Q} onChange={calQ} className="Q" type="text" name="Q" maxLength="4" />л/с</li>
               <li> Н – робочий напір  <input id="inp" value={Н} onChange={calН} className="eH" type="text" name="Н" maxLength="4" />м</li>
               <li> η<sub>T</sub> – повний ККД турбіни  <input value={ηt} onChange={calηt} className="ηt" type="text" name="ηt" readOnly /></li>
            </ul>
            <input id="inp" className="KKD" type="range" max="95" step="5" onChange={calηt} name="KKD" />
            <label className="LabelTypeOfTurbins">Рекомендована турбіна під ККД: {nameoftype}</label>
            <div className="TypeTurbinsImage">   <img className="TypeTurbinsCurrentImg" src={sourceimage} alt="Тип Турбіни" /></div>
            <div className="Formula№1Formulas">
               <label> P<sub>т</sub> = {!ηt ? <span>&rho;</span> : 1000}  ⋅
   {!ηt ? <span>g</span> : 9.81}  ⋅
   {!Q ? <span>Q</span> : Q}  ⋅
    {!Н ? <span>Н</span> : Н}  ⋅
    {!ηt ? <span>η <sub>T</sub></span> : ηt}</label>
            </div>
         </div>



         <div className="ElectricPowerGenerator">

            <h1 className="Formula№2Header" > Електрична потужність генератора <input value={Pe} className="Formula№2Result" type="number" name="Pe" readOnly /> Вт  </h1>
            <div className="Formula№2Variables">

               <li> &rho; – густина води <input value={hro} className="hro" type="text" name="hro" readOnly />кг/м<sup>3</sup> </li>
               <li> g – прискорення вільного падіння <input value={g} className="g" type="text" name="g" readOnly />м/c<sup>2</sup></li>
               <li> Q – витрата води <input value={Q1} onChange={calQ1} className="Q" type="text" name="Q" maxLength="4" />л/с</li>
               <li> Н – робочий напір  <input value={Н1} onChange={calН1} className="eH" type="text" name="Н1" maxLength="4" />м</li>
               <li>  h <sub>ƨ</sub> – гідравлічні втрати  <input value={hg} onChange={calhg} className="hg" type="text" maxLength="4" /></li>
            </div>
            <div className="Formula№2Variables№2">
               <li>  h <sub>j</sub> – інші втрати  <input value={hj} onChange={calhj} className="hj" type="text" maxLength="4" /></li>
               <li>  e<sub>t</sub> – ефективність генератора  <input value={et} onChange={calet} className="et" type="text" maxLength="4" /></li>
               <li>  ƒ<sub>t</sub> – втрати трансформатора  <input value={ƒt} onChange={calƒt} className="ƒt" type="text" maxLength="4" /></li>
               <li>  ƒ<sub>i</sub> – інші втрати електроенергії  <input value={ƒi} onChange={calƒi} className="ƒi" type="text" maxLength="4" /></li>
               <li>  e<sub>g</sub> – коефіцієнт використання при СБС  Q  <input value={eg} onChange={caleg} className="eg" type="text" maxLength="4" /></li>
            </div>

            <div className="Formula№2Formulas">
               <label> P<sub>E</sub> = {!ƒi ? <span>&rho;</span> : 1000} ⋅ {!ƒi ? <span>g</span> : 9.81} ⋅ {!Q1 ? <span>Q</span> : Q1} ⋅ [{!Н1 ? <span>H</span> : Н1} -
      ({!hg ? <span>h<sub>g</sub></span> : hg} + {!hj ? <span>h<sub>j</sub></span> : hj} )] ⋅ {!et ? <span>e<sub>t</sub></span> : et} ⋅ {!eg ? <span>e<sub>g</sub></span> : eg} ⋅
      (1 - {!ƒt ? <span>ƒ<sub>t</sub></span> : ƒt}) ⋅ (1 - {!ƒi ? <span>ƒ<sub>i</sub></span> : ƒi}) </label>
            </div>
         </div>

         <div className="TCR">
            <h1 className="Formula№3Header" > Коефіцієнт стиснення струменя рідини <input value={ε} className="Formula№3Result" type="number" name="ε" readOnly /> м<sup>2</sup>  </h1>
            <div className="Formula№3Variables">
               <li> S<sub>c</sub> – стиснений переріз струменя <input value={Sc} onChange={calSc} className="Sc" type="text" name="hro" maxLength="4" />м<sup>2</sup> </li>
               <li> S – переріз донного отвору <input value={S} onChange={calS} className="S" type="text" name="S" maxLength="4" />м<sup>2</sup></li>
            </div>
            <label className="Formula№3Formulas"> ε = </label>
            <div className="divide">
               <label className="top">{!Sc ? <span>S<sub>c</sub></span> : Sc}</label>
            </div>
            <label className="bottom" >{!S ? <span>S</span> : S}</label>

         </div >
         <div className="Costfactor">

            <h1 className="Formula№4Header"> Коефіцієнт витрати <input value={μH} className="Formula№4Result" type="number" name="μH" readOnly /> </h1>
            <div className="Formula№4Variables">
               <li> ε – Коефіцієнт стиснення струменя рідини <input value={ε1} onChange={calε1} className="ε1" type="text" name="hro" maxLength="4" /> </li>
               <li> φ<sub>H</sub> – <input value={φH} onChange={calφH} className="φH" type="text" name="φH" maxLength="4" /> </li>
            </div>



            <label className="Formula№4Formulas" > μ<sub>H</sub> = {!ε1 ? <span>ε</span> : ε1} ⋅ {!φH ? <span>φH</span> : φH}</label>


         </div>


         <div>
            <button onClick={eventSaving} className="ButtonDownloadResult"> Завантажити </button>
         </div>


      </div>
   </>
}


export default PW
