import React, {useEffect, useState} from 'react';

var input = document.querySelectorAll('.input'),
  buffer = [];
for (var i = 0; input.length > i; i++) {
  console.log(input[i].value);
  buffer[i] = document.createElement('div');
  buffer[i].className = "buffer";
  input[i].parentNode.insertBefore(buffer[i], input[i].nextSibling);
  input[i].oninput = function() {
    this.nextElementSibling.innerHTML = this.value;
    this.style.width = this.nextElementSibling.clientWidth + 'px';
  };
}


const ElectricPowerGenerator = () => {
    let sourceimage = ""
let nameoftype = ""
    const[hro, sethro] = useState()
    const[g, setg] = useState()
    const[Q, setQ] = useState()
    const[Н, setН] = useState()
    const[ηt, setηt] = useState()
    const[Pt, setPt] = useState()
    function calhro(e) { sethro(e.target.value)}
    function calg(e){setg(e.target.value)}
    function calQ(e){setQ(e.target.value)}
    function calН(e){setН(e.target.value)}
    function calηt(e){
        setηt(e.target.value)}
        useEffect(()=> {
            setPt(Number(hro)*Number(g)*Number(Q)*Number(Н)*Number(ηt))
        },[ηt , hro , g , Q , Н ])

    return <>

    <h1 className="Formula№1Header" >Потужність, яка розвивається гідротурбіною <input   value= {Pt}  className="Formula№1Result" type="number" name="Pt" placeholder="Pt"/>  Вт</h1>
    
    <div className="Formula№1Variables">
    <li>
         &rho; = 1000 кг/м3 – густина води: 
          g = 9,81 м/с2 – прискорення вільного падіння 
        Q – витрата води 
        Н – робочий напір 
         η<sub>T</sub> – повний ККД турбіни 
        <label>{nameoftype}</label>
        <img  src={sourceimage} className="typeturbins" alt="Тип Турбіни"/>
    </li>
   </div>
   
   <div className="Formula№1Formulas"> 
   <label > P<sub>т</sub> </label>
   = <input value = {hro} onChange={calhro} className="hro" type="number" name="hro"  placeholder="&rho;"/>
    ⋅ <input  value = {g} onChange={calg} className="input1" type="number" name="g"  placeholder="g" />
     ⋅ <input value = {Q} onChange={calQ} className="input1" type="number" name="Q"  placeholder="Q" />
      ⋅ <input  value = {Н} onChange={calН} className="input1" type="number" name="Н"  placeholder="Н" />
       ⋅ <input  value =  {ηt} onChange={calηt}className="input1" type="range" name="ηt"  max="95" step="5"   />
       </div>
    
    
    </> 
 }


 export default ElectricPowerGenerator
